    b[n].buffer = i.name_po_value.data ();
    b[n].capacity = i.name_po_value.capacity ();
    b[n].size = &i.name_po_size;
    b[n].is_null = &i.name_po_null;
    n++;

    // category
    //
    b[n].type = pgsql::bind::bigint;
    b[n].buffer = &i.category_value;
    b[n].is_null = &i.category_null;
    n++;
