  bool access::object_traits_impl< ::VersionPO, id_pgsql >::
  grow (image_type& i,
        bool* t)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (t);

    bool grew (false);

    // id
    //
    t[0UL] = 0;

    // version
    //
    if (t[1UL])
    {
      i.version_value.capacity (i.version_size);
      grew = true;
    }

    // date_version
    //
    if (t[2UL])
    {
      i.date_version_value.capacity (i.date_version_size);
      grew = true;
    }

    // po
    //
    t[3UL] = 0;

    return grew;
  }

  void access::object_traits_impl< ::VersionPO, id_pgsql >::
  bind (pgsql::bind* b,
        image_type& i,
        pgsql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace pgsql;

    std::size_t n (0);

    // id
    //
    if (sk != statement_insert && sk != statement_update)
    {
      b[n].type = pgsql::bind::bigint;
      b[n].buffer = &i.id_value;
      b[n].is_null = &i.id_null;
      n++;
    }

    // version
    //
    b[n].type = pgsql::bind::text;
    b[n].buffer = i.version_value.data ();
    b[n].capacity = i.version_value.capacity ();
    b[n].size = &i.version_size;
    b[n].is_null = &i.version_null;
    n++;

    // date_version
    //
    b[n].type = pgsql::bind::text;
    b[n].buffer = i.date_version_value.data ();
    b[n].capacity = i.date_version_value.capacity ();
    b[n].size = &i.date_version_size;
    b[n].is_null = &i.date_version_null;
    n++;

    // po
    //
    b[n].type = pgsql::bind::bigint;
    b[n].buffer = &i.po_value;
    b[n].is_null = &i.po_null;
    n++;
  }

