  bool access::object_traits_impl< ::VersionPO, id_pgsql >::
  init (image_type& i,
        const object_type& o,
        pgsql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace pgsql;

    bool grew (false);

    // version
    //
    {
      ::std::string const& v =
        o.version;

      bool is_null (false);
      std::size_t size (0);
      std::size_t cap (i.version_value.capacity ());
      pgsql::value_traits<
          ::std::string,
          pgsql::id_string >::set_image (
        i.version_value,
        size,
        is_null,
        v);
      i.version_null = is_null;
      i.version_size = size;
      grew = grew || (cap != i.version_value.capacity ());
    }

    // date_version
    //
    {
      ::std::string const& v =
        o.date_version;

      bool is_null (false);
      std::size_t size (0);
      std::size_t cap (i.date_version_value.capacity ());
      pgsql::value_traits<
          ::std::string,
          pgsql::id_string >::set_image (
        i.date_version_value,
        size,
        is_null,
        v);
      i.date_version_null = is_null;
      i.date_version_size = size;
      grew = grew || (cap != i.date_version_value.capacity ());
    }

    // po
    //
    {
      ::std::shared_ptr< ::PO > const& v =
        o.po;

      typedef object_traits< ::PO > obj_traits;
      typedef odb::pointer_traits< ::std::shared_ptr< ::PO > > ptr_traits;

      bool is_null (ptr_traits::null_ptr (v));
      if (!is_null)
      {
        const obj_traits::id_type& id (
          obj_traits::id (ptr_traits::get_ref (v)));

        pgsql::value_traits<
            obj_traits::id_type,
            pgsql::id_bigint >::set_image (
          i.po_value, is_null, id);
        i.po_null = is_null;
      }
      else
        throw null_pointer ();
    }

    return grew;
  }

  void access::object_traits_impl< ::VersionPO, id_pgsql >::
  init (object_type& o,
        const image_type& i,
        database* db)
  {
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (db);

    // id
    //
    {
      long unsigned int& v =
        o.id;

      pgsql::value_traits<
          long unsigned int,
          pgsql::id_bigint >::set_value (
        v,
        i.id_value,
        i.id_null);
    }

    // version
    //
    {
      ::std::string& v =
        o.version;

      pgsql::value_traits<
          ::std::string,
          pgsql::id_string >::set_value (
        v,
        i.version_value,
        i.version_size,
        i.version_null);
    }

    // date_version
    //
    {
      ::std::string& v =
        o.date_version;

      pgsql::value_traits<
          ::std::string,
          pgsql::id_string >::set_value (
        v,
        i.date_version_value,
        i.date_version_size,
        i.date_version_null);
    }

    // po
    //
    {
      ::std::shared_ptr< ::PO >& v =
        o.po;

      typedef object_traits< ::PO > obj_traits;
      typedef odb::pointer_traits< ::std::shared_ptr< ::PO > > ptr_traits;

      if (i.po_null)
        v = ptr_traits::pointer_type ();
      else
      {
        obj_traits::id_type id;
        pgsql::value_traits<
            obj_traits::id_type,
            pgsql::id_bigint >::set_value (
          id,
          i.po_value,
          i.po_null);

        // If a compiler error points to the line below, then
        // it most likely means that a pointer used in a member
        // cannot be initialized from an object pointer.
        //
        v = ptr_traits::pointer_type (
          static_cast<pgsql::database*> (db)->load<
            obj_traits::object_type > (id));
      }
    }
  }

