  bool access::object_traits_impl< ::PC, id_pgsql >::
  grow (image_type& i,
        bool* t)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (t);

    bool grew (false);

    // id
    //
    t[0UL] = 0;

    // name_pc
    //
    if (t[1UL])
    {
      i.name_pc_value.capacity (i.name_pc_size);
      grew = true;
    }

    // department
    //
    t[2UL] = 0;

    return grew;
  }

  void access::object_traits_impl< ::PC, id_pgsql >::
  bind (pgsql::bind* b,
        image_type& i,
        pgsql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace pgsql;

    std::size_t n (0);

    // id
    //
    if (sk != statement_insert && sk != statement_update)
    {
      b[n].type = pgsql::bind::bigint;
      b[n].buffer = &i.id_value;
      b[n].is_null = &i.id_null;
      n++;
    }

    // name_pc
    //
    b[n].type = pgsql::bind::text;
    b[n].buffer = i.name_pc_value.data ();
    b[n].capacity = i.name_pc_value.capacity ();
    b[n].size = &i.name_pc_size;
    b[n].is_null = &i.name_pc_null;
    n++;

    // department
    //
    b[n].type = pgsql::bind::bigint;
    b[n].buffer = &i.department_value;
    b[n].is_null = &i.department_null;
    n++;
  }

  void access::object_traits_impl< ::PC, id_pgsql >::
  bind (pgsql::bind* b, id_image_type& i)
  {
    std::size_t n (0);
    b[n].type = pgsql::bind::bigint;
    b[n].buffer = &i.id_value;
    b[n].is_null = &i.id_null;
  }

  bool access::object_traits_impl< ::PC, id_pgsql >::
  init (image_type& i,
        const object_type& o,
        pgsql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace pgsql;

    bool grew (false);

    // name_pc
    //
    {
      ::std::string const& v =
        o.name_pc;

      bool is_null (false);
      std::size_t size (0);
      std::size_t cap (i.name_pc_value.capacity ());
      pgsql::value_traits<
          ::std::string,
          pgsql::id_string >::set_image (
        i.name_pc_value,
        size,
        is_null,
        v);
      i.name_pc_null = is_null;
      i.name_pc_size = size;
      grew = grew || (cap != i.name_pc_value.capacity ());
    }

    // department
    //
    {
      ::std::shared_ptr< ::Department > const& v =
        o.department;

      typedef object_traits< ::Department > obj_traits;
      typedef odb::pointer_traits< ::std::shared_ptr< ::Department > > ptr_traits;

      bool is_null (ptr_traits::null_ptr (v));
      if (!is_null)
      {
        const obj_traits::id_type& id (
          obj_traits::id (ptr_traits::get_ref (v)));

        pgsql::value_traits<
            obj_traits::id_type,
            pgsql::id_bigint >::set_image (
          i.department_value, is_null, id);
        i.department_null = is_null;
      }
      else
        throw null_pointer ();
    }

    return grew;
  }

  void access::object_traits_impl< ::PC, id_pgsql >::
  init (object_type& o,
        const image_type& i,
        database* db)
  {
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (db);

    // id
    //
    {
      long unsigned int& v =
        o.id;

      pgsql::value_traits<
          long unsigned int,
          pgsql::id_bigint >::set_value (
        v,
        i.id_value,
        i.id_null);
    }

    // name_pc
    //
    {
      ::std::string& v =
        o.name_pc;

      pgsql::value_traits<
          ::std::string,
          pgsql::id_string >::set_value (
        v,
        i.name_pc_value,
        i.name_pc_size,
        i.name_pc_null);
    }

    // department
    //
    {
      ::std::shared_ptr< ::Department >& v =
        o.department;

      typedef object_traits< ::Department > obj_traits;
      typedef odb::pointer_traits< ::std::shared_ptr< ::Department > > ptr_traits;

      if (i.department_null)
        v = ptr_traits::pointer_type ();
      else
      {
        obj_traits::id_type id;
        pgsql::value_traits<
            obj_traits::id_type,
            pgsql::id_bigint >::set_value (
          id,
          i.department_value,
          i.department_null);

        // If a compiler error points to the line below, then
        // it most likely means that a pointer used in a member
        // cannot be initialized from an object pointer.
        //
        v = ptr_traits::pointer_type (
          static_cast<pgsql::database*> (db)->load<
            obj_traits::object_type > (id));
      }
    }
  }

